import React, { Component } from "react";
import { Link } from 'react-router-dom';

import axios from "axios";


class Posts extends Component {

    state = {
        posts: [],
        status: null
    };

    componentDidMount() {
        this.getPosts();
    }

    getPosts = () => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(endpoint)
            .then(res => {
                this.setState({
                    posts: res.data,
                    status: 'success'
                });
            }).catch(err => {
                this.setState({
                    posts: [],
                    status: 'failed'
                });
            });
    };

    render() {

        if (this.state.posts.length > 0) {
            let listPosts = this.state.posts.map((post, index) => {
                return (
                    <div key={index}>
                        <Link to={'/comments/'+post.id}>  
                            <h4><strong>{post.title.toUpperCase()}</strong></h4>
                        </Link>
                        <h5 className="mt-2"><i><strong>{post.body}</strong></i></h5>
                        <hr />
                    </div>
                );
            });

            return (
                <div className="mt-3 p-5">
                    <h1 className="text-center">ἐπί-γραφὼ</h1>
                    <hr />
                    {listPosts}

                </div>
            );
        }

        return (
            <h3>cargando...</h3>
        );
    }

}

export default Posts;
