import React, { Component } from "react";
import axios from "axios";


class Comments extends Component {

    state = {
        comments: [],
        status: null
    };


    componentDidMount() {

        console.log(this.props.match.params.id)
        let postId = this.props.match.params.id;
       // this.getComments(postId);

    }


    getComments = (postId) => {
        let endpoint = 'https://jsonplaceholder.typicode.com/post/' + postId.toString() + '/comments';
        axios.get(endpoint)
            .then(res => {
                this.setState({
                    comments: res.data,
                    status: 'success'
                });
            }).catch(err => {
                this.setState({
                    comments: [],
                    status: 'failed'
                });
            });
    };


    render() {

        if(this.state.comments.length>0){
            let listComment = this.state.comments.map( (comment, index) =>{
                return(
                    <div key={index}>
                        {comment.email} <br/>
                        <strong>{comment.name.toUpperCase()}</strong> <br/>
                        <i>{comment.body}</i> <br/>
                        <hr/>
                    </div>

                );

            });

            return(
                <div>{listComment}</div>
            );
        }



        return (
            <h1>comments</h1>

        );
    }
}


export default Comments;
