import axios from "axios";


class Comments extends Component {

    state = {
        comments: [],
        status: null
    };


    componentDidMount() {
        this.getAllComments();

    }


    getComments = (postId) => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts' + postId.toString() + 'comments';
        axios.get(endpoint)
            .then(res => {
                this.setState({
                    comments: res.data,
                    status: 'success'
                });
            }).catch(err => {
                this.setState({
                    comments: [],
                    status: 'failed'
                });
            });
    };


    render() {

        if(this.state.comments.length>0){
