import React, { Component } from "react";

import axios from "axios";


class Posts extends Component {

    state = {
        posts: [],
        status: null
    };

    componentWillMount() {
        this.getPosts();
    }



    getPosts = () => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts';

        axios.get('https://jsonplaceholder.typicode.com/posts')
            .then(res => {
                this.setState({
                    posts: res.data,
                    status: 'success'
                });

            }).catch(err => {
                this.setState({
                    posts: [],
                    status: 'failed'
                });
            });
    };

    render() {

        if (this.state.posts.length > 0) {

            var listPosts = this.state.posts.map((post, index) => {
                console.log(post.title);
                return (
                    <div>

                        <h2>{post.title}</h2>
                        <h3>{post.body}</h3>




                        <hr />


                    </div>

                );
            });

        }



        return (
            <div className="mt-4">


                <h1>este es el post</h1>
                <h1> {this.state.posts.lenght}</h1>

                {listPosts}



            </div>

        );
    }

}

export default Posts;
