import React, { Component } from "react";
import { Link } from 'react-router-dom';

import axios from "axios";


class Posts extends Component {

    state = {
        posts: [],
        status: null,
        currentPage: 0,
        stepSize: 5
    };

    componentDidMount() {
        this.getPosts();
    }

    getPosts = () => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(endpoint)
            .then(res => {
                this.setState({
                    posts: res.data,
                    status: 'success'
                });
            }).catch(err => {
                this.setState({
                    posts: [],
                    status: 'failed'
                });
            });
    };

    increasePage = () => {

        let stepSize = this.state.stepSize;
        let nextPage = this.state.currentPage + 1;
        alert(this.state.posts.lenght);
        if(nextPage*stepSize < this.state.posts.lenght){
            alert('en el if');
            this.setState({
                currentPage: 5
            });
        }
        console.log(this.state.currentPage);

    };

    render() {

        let posts = this.state.posts;
        let stepSize = this.state.stepSize;
        let strPostIndex = stepSize * this.state.currentPage;

        if (this.state.posts.length > 0) {
            let listPosts = posts.slice(strPostIndex, strPostIndex + stepSize)
                .map((post, index) => {
                    return (
                        <div key={index}>
                            <Link to={'/comments/' + post.id}>
                                <h4 className="mt-4"><strong>{post.title.toUpperCase()}</strong></h4>
                            </Link>
                            <h5 className="my-4"><i><strong>{post.body}</strong></i></h5>
                            <hr />
                        </div>
                    );
                });

            return (
                <div className="mt-3 p-5">
                    <h1 className="text-center">ἐπί-γραφὼ</h1>
                    <button onClick={this.increasePage}>++</button>
                    <hr />
                    {listPosts}

                </div>
            );
        }

        return (
            <h3>cargando...</h3>
        );
    }

}

export default Posts;
