import React, { Component } from "react";
import axios from 'axios';



class Search extends Component {

    state = {
        query: null,
        posts: [],
        satus: null
    };

    componentDidMount() {
        this.getPosts();
        this.getQuery();
    }


    getPosts = () => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(endpoint)
            .then((res) => {
                this.setState({
                    posts: res.data,
                    status: 'success'
                });
            }).catch((err) => {
                this.setState({
                    posts: [],
                    status: 'failed'
                });
            });
    };

    getQuery = () => {
        let query = this.props.match.params.query;
        this.setState({
            query: query
        });
    };

    getFilteredList = () => {
        let posts = this.state.posts;
        let query = this.state.query;

        let filteredList = posts.map((post) => {
            if(post.title.includes(query)){
                return post;

            }
            return;


        });


        return filteredList;


    };



    render() {

        if (this.state.posts.length > 0) {
            let filteredList = this.getFilteredList();
            console.log(filteredList);

            return (
                <div className="container canvas">

                    <h3>{this.state.query}</h3>

                    <p>{filteredList}</p>

                </div>
            );
        }

        return (
            <div className="container canvas">
                <h3>cargando...</h3>
            </div>
        );
    };
}


export default Search;
