import React, { Component } from "react";
import axios from 'axios';



class Search extends Component {

    state = {
        query: null,
        posts: [],
        satus: null
    };

    componentDidMount() {
        this.getPosts();
        this.getQuery();
    }


    getPosts = () => {
        let endpoint = 'https://jsonplaceholder.typicode.com/posts';
        axios.get(endpoint)
            .then((res) => {
                this.setState({
                    posts: res.data,
                    status: 'success'
                });
            }).catch((err) => {
                this.setState({
                    posts: [],
                    status: 'failed'
                });
            });
    };

    getQuery = () => {
        let query = this.props.match.params.query;
        this.setState({
            query: query
        });
    };

    getFilteredList = () => {
        let posts = this.state.posts;
        let query = this.state.query;

        let filteredList = posts.filter((post) => {
            return (post.title.includes(query) || post.body.includes(query));
        });

        return filteredList;
    };



    render() {

        if (this.state.posts.length > 0) {
            let filteredList = this.getFilteredList();

            if (filteredList.length == 0) {
                return (
                    <div className="container canvas">
                        <h3>No se han encontrado elementos...</h3>

                    </div>

                );
            }

            let filteredPosts = filteredList.map((post, index) => {
                return (
                    <div key={index}>

                        <h4>{post.title.toUpperCase()}</h4>

                        <h5>{post.body}</h5>


                        <hr />


                    </div>
                );
            });

            return (
                <React.Fragment >
                    <div className="container canvas">

                        <h3 className="text-center mt-3 p-5">"{this.state.query}"</h3>

                    </div>

                    <div className="container canvas">

                        {filteredPosts}

                    </div>

                </React.Fragment>
            );
        }

        return (
            <div className="container canvas">
                <h3>cargando...</h3>
            </div>
        );
    };
}


export default Search;
